import React from 'react';
import logo from '../components/images/logo.png';
import Button from '../components/buttons/Button';
import {HiSearch,HiMail,HiBell} from 'react-icons/hi';
import avatar from '../components/images/avatar.png'



const Header=()=>{
    return(
        <header style ={styles.header}>
        <div style ={styles.ltNav}>
            <img style ={styles.logo} src={logo} alt="Logo" />
            <h1 style={styles.h1}>ClearSkies</h1>
        </div>
        <form style={styles.searchContiner} >
        <input style={styles.input} type="text" placeholder="Search"  /> 
        <Button btnText={<HiSearch style={styles.button}/>} />

        </form>
        <div style={styles.rtNav}>
            <img style={styles.avatar} src={avatar} alt="Logo"/>
            <HiMail style={styles.inputIcon}/>
            <HiBell style={styles.inputIcon}/>

        </div>
        </header>
    )
}
export default Header

const styles ={

    header: {
            display: 'flex',
            flexDirection: 'row',
            justifyContent: 'space-between',
            alignItems: 'center',
            paddingLeft: 20,
            paddingRight: 20,
            paddingTop: 10,
            paddingBotom: 10,
            backgroundColor: 'black',
            color: 'gray'
        },
    ltNav: {
            display: 'flex',
            alignItems: 'center',

        },
    left:{
        display: 'flex',
        flexDirection: 'row',

    },
    logo:{
        borderRadius: '50%',
        height: '50px',
        width: '60px',
        marginRight: '10%',
        alignItems: 'center' 
    },
    avatar:{
        height: '40px',
        width: '40px',
        border: '1px solid #eefc57',
        borderRadius: '50%',

    },
    searchContainer:{
        border: '1px solid #eefc57',
        display: 'flex',
        flexDirection: 'row',
        backgroundColor:'gray'

    },
    inputIcon:{

        padding: '10px',
        display: 'flex',
        alignItems: 'center',
        fontSize: '1rem'
    },
    input:{
        border: '1px solid #eefc57',
        borderRadius: '10%',
        flex: 1,
        width: '70%',
        padding: '10px',
        backgroundColor: 'black',
        color: 'white'

    },
    rtNav: {
        display: 'flex',
        flexDirection: 'row',
        alignItems: 'center',
        marginRight: '2%',
        justifyContent: 'space-between'

    },
    h1: {
     display: 'flex',
     flexDirection: 'row',
     alignItems: 'center',
     fontSize: '1rem',
     color: '#5fbff9'

    },
    button:{
        display: 'flex',
        fontSize: '14px'
    }

 }
